package org.bharath.problemsolving.qspidersjavaproblems;

public class FindingAreaAndCircumferenceOfACircle {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int radius = 10;
		double pie = 3.14;
		
		double area = pie*radius*radius;
		double circum = 2*pie*radius;
				
		System.out.println("The area of the circle is "+area+" and the circumference of the circle is "+circum);
	}

}
